window.addEventListener("load",function(){const e=document.querySelectorAll(".image-api"),t=document.querySelector("#species"),n=document.querySelector("#language"),r=document.querySelector("#average-life-span"),c=document.querySelector("#character-img"),o=document.querySelector("#character-name"),a=document.querySelector("#birth_year");for(let i=0;i<e.length;i++)e[i].onclick=function(){let e=this.getAttribute("data-img"),i=this.getAttribute("src");c.setAttribute("src",i),fetch(`https://swapi.co/api/people/${e}`).then(e=>e.json()).then(e=>{console.log(e),o.innerHTML=e.name,a.innerHTML=e.birth_year,fetch(e.species).then(e=>e.json()).then(e=>{console.log(e),t.innerHTML=e.name,n.innerHTML=e.language,r.innerHTML=e.average_lifespan})})}});